              "id": "04-website_site_app-js",
              "name": "app.js",
              "label": "JavaScript",
              "type": "js",
              "path": "04-website/site/app.js",
              "content": "/* ============================================================\n   TALLOW NATIVE — Main JavaScript\n   ============================================================ */\n\ndocument.addEventListener('DOMContentLoaded', () => {\n\n  // --- NAV SCROLL EFFECT ---\n  const nav = document.getElementById('main-nav');\n  window.addEventListener('scroll', () => {\n    if (window.scrollY > 40) {\n      nav.classList.add('scrolled');\n    } else {\n      nav.classList.remove('scrolled');\n    }\n  });\n\n  // --- MOBILE HAMBURGER ---\n  const hamburger = document.getElementById('hamburger');\n  const mobileMenu = document.getElementById('mobile-menu');\n  hamburger.addEventListener('click', () => {\n    mobileMenu.classList.toggle('open');\n    const isOpen = mobileMenu.classList.contains('open');\n    hamburger.setAttribute('aria-expanded', isOpen);\n    // Animate hamburger to X\n    const spans = hamburger.querySelectorAll('span');\n    if (isOpen) {\n      spans[0].style.transform = 'rotate(45deg) translate(5px, 5px)';\n      spans[1].style.opacity = '0';\n      spans[2].style.transform = 'rotate(-45deg) translate(5px, -5px)';\n    } else {\n      spans[0].style.transform = '';\n      spans[1].style.opacity = '';\n      spans[2].style.transform = '';\n    }\n  });\n\n  // Close mobile menu on link click\n  mobileMenu.querySelectorAll('a').forEach(link => {\n    link.addEventListener('click', () => {\n      mobileMenu.classList.remove('open');\n      hamburger.querySelectorAll('span').forEach(s => {\n        s.style.transform = '';\n        s.style.opacity = '';\n      });\n    });\n  });\n\n  // --- FAQ ACCORDION ---\n  const faqButtons = document.querySelectorAll('.faq-q');\n  faqButtons.forEach(btn => {\n    btn.addEventListener('click', () => {\n      const isExpanded = btn.getAttribute('aria-expanded') === 'true';\n      // Close all\n      faqButtons.forEach(b => {\n        b.setAttribute('aria-expanded', 'false');\n        b.nextElementSibling.classList.remove('open');\n      });\n      // Open clicked (if it was closed)\n      if (!isExpanded) {\n        btn.setAttribute('aria-expanded', 'true');\n        btn.nextElementSibling.classList.add('open');\n      }\n    });\n  });\n\n  // --- SMOOTH SCROLL FOR ANCHOR LINKS ---\n  document.querySelectorAll('a[href^=\"#\"]').forEach(anchor => {\n    anchor.addEventListener('click', e => {\n      const target = document.querySelector(anchor.getAttribute('href'));\n      if (target) {\n        e.preventDefault();\n        const navHeight = nav.offsetHeight;\n        const targetTop = target.getBoundingClientRect().top + window.scrollY - navHeight - 16;\n        window.scrollTo({ top: targetTop, behavior: 'smooth' });\n      }\n    });\n  });\n\n  // --- RESERVE FORM ---\n  const reserveForm = document.getElementById('reserve-form');\n  if (reserveForm) {\n    reserveForm.addEventListener('submit', e => {\n      e.preventDefault();\n      const email = document.getElementById('email').value;\n      if (email) {\n        // In production: send to email service (Klaviyo, Mailchimp, etc.)\n        // For now: show confirmation\n        reserveForm.innerHTML = `\n          <div style=\"text-align:center; padding: 32px;\">\n            <div style=\"font-size: 48px; margin-bottom: 16px;\">✓</div>\n            <h3 style=\"color: var(--cream); font-family: var(--font-serif); font-size: 24px; margin-bottom: 12px;\">\n              You're on the list.\n            </h3>\n            <p style=\"color: rgba(245,237,216,0.7); font-size: 16px; line-height: 1.6;\">\n              We'll email <strong style=\"color:var(--cream)\">${email}</strong> when your reservation is confirmed.<br>\n              First run ships [DATE].\n            </p>\n          </div>\n        `;\n      }\n    });\n  }\n\n  // --- INTERSECTION OBSERVER: Fade-in on scroll ---\n  const fadeElements = document.querySelectorAll('.piece, .step, .spec-callout, .faq-item');\n  const fadeObserver = new IntersectionObserver((entries) => {\n    entries.forEach(entry => {\n      if (entry.isIntersecting) {\n        entry.target.style.opacity = '1';\n        entry.target.style.transform = 'translateY(0)';\n        fadeObserver.unobserve(entry.target);\n      }\n    });\n  }, { threshold: 0.1, rootMargin: '0px 0px -40px 0px' });\n\n  fadeElements.forEach(el => {\n    el.style.opacity = '0';\n    el.style.transform = 'translateY(24px)';\n    el.style.transition = 'opacity 0.6s ease, transform 0.6s ease';\n    fadeObserver.observe(el);\n  });\n\n  // --- ANIMATED SYSTEM DIAGRAM: Fat Drop ---\n  // Animate the copper arrow/dot between pot and catcher in the SVG\n  const svgDiagram = document.querySelector('.system-diagram');\n  if (svgDiagram) {\n    let dropActive = false;\n    svgDiagram.addEventListener('mouseenter', () => {\n      if (dropActive) return;\n      dropActive = true;\n      const polygon = svgDiagram.querySelector('polygon');\n      if (polygon) {\n        polygon.style.animation = 'dropBounce 1.5s ease infinite';\n      }\n    });\n    svgDiagram.addEventListener('mouseleave', () => {\n      dropActive = false;\n      const polygon = svgDiagram.querySelector('polygon');\n      if (polygon) {\n        polygon.style.animation = '';\n      }\n    });\n  }\n\n  // --- ACTIVE NAV LINK (scroll spy) ---\n  const sections = document.querySelectorAll('section[id]');\n  const navLinks = document.querySelectorAll('.nav-links a');\n  \n  const sectionObserver = new IntersectionObserver((entries) => {\n    entries.forEach(entry => {\n      if (entry.isIntersecting) {\n        navLinks.forEach(link => {\n          link.classList.remove('active');\n          if (link.getAttribute('href') === `#${entry.target.id}`) {\n            link.classList.add('active');\n          }\n        });\n      }\n    });\n  }, { rootMargin: '-50% 0px -50% 0px' });\n\n  sections.forEach(section => sectionObserver.observe(section));\n\n  // --- INJECT ANIMATION CSS ---\n  const styleSheet = document.createElement('style');\n  styleSheet.textContent = `\n    @keyframes dropBounce {\n      0%, 100% { transform: translateY(0); }\n      50% { transform: translateY(8px); }\n    }\n    .nav-links a.active { color: var(--copper); }\n  `;\n  document.head.appendChild(styleSheet);\n\n  console.log('Tallow Native — Built for the fryer. Zero plastic.');\n});\n"
